var fcdata = {};
var units=flow.get('units');
var degreesToCardinal = function(deg){
if (deg>22.50 && deg<=67.50){
return "north_east";
  }else if (deg>67.51 && deg<112.50){
return "east";
  }else if (deg>112.51 && deg<157.50){
return "south_east";
  }else if (deg>157.51 && deg<202.50){
return "south";
  }else if (deg>202.51 && deg<247.50){
return "south_west";
  }else if (deg>247.51 && deg<292.50){
return "west";
  }else if (deg>292.51 && deg<337.50){
return "north_west";
  }else{
return "north"; 
  }
}
if (units===undefined)
{
  units="imperial";
}
function formatTemp(high, low){
    if (units == "imperial") {
        if (low){
          temp = parseFloat(high).toFixed() + '|' + parseFloat(low).toFixed()
        }
        else {
          temp = parseFloat(high).toFixed() + '°F'
        }
    }
    else { // metric
//        temp = parseFloat(temp).toFixed(1) + '°'
        if (low){
          temp = parseFloat(high).toFixed() + '°|' + parseFloat(low).toFixed()+'°'
        }
        else {
          temp = parseFloat(high).toFixed() + '°'
        }
    }
    return temp;
}
function dayName(unixTime){
    var d = new Date(unixTime * 1000);
    var weekday = new Array(7);
        weekday[0] = "Søn";
        weekday[1] = "Man";
        weekday[2] = "Tirs";
        weekday[3] = "Ons";
        weekday[4] = "Tors";
        weekday[5] = "Fre";
        weekday[6] = "Lør";

    return weekday[d.getDay()]
}
function timeConvert(UNIX_timestamp){
  var a = new Date(UNIX_timestamp * 1000);
  var hour = a.getHours();
  //var suffix = " am";
  //if (hour >= 12) {
 //   hour = hour - 12;
  //  suffix = " pm";
 // }
  //if (hour === 0) {
  //  hour = 12;
 // }
  var min = a.getMinutes();
  if (min < 10) {min = "0" + min;}
  return hour + ':' + min; //+ suffix;
  return hour;
}
// prepare forecast data for CSS based ui widget

